mapper.select:
use forcastflood;
select 
from para_calplan
where Id = #{id}
order by Id desc

use forcastflood;
select Id, PDNUM, FLOWTM,Q,W,ZI,V
from MOD_FRUIT
where Id = #{id}
and PDNUM between #{startTime} and #{endTime}
order by PDNUM asc

use forcastflood;
select Id,PDNUM,FLOWTM,Q,P
from Mod_MidResult
where Id = #{id}
and CalRegCD = #{calRegCD}
and PDNUM between #{startTime} and #{endTime}
order by PDNUM asc

mapper.update:
use forcastflood;
update mod_fruit

    set Q = #{data1}, W = #{data2} where Id = #{id} and FLOWTM = #{flowtm}


    set ZI = #{data1}, V = #{data2} where Id = #{id} and FLOWTM = #{flowtm}

use forcastflood;
update PARA_FORECAST set A= #{p1},DATT= #{p2},B= #{p3},IM= #{p4},KI= #{p5},KG= #{p6},EX= #{p7},CI= #{p8},CG= #{p9},CS= #{p10},L= #{p11},X= #{p12},SM= #{p13},WUM= #{p14},WLM= #{p15},WDM= #{p16},C= #{p17},K= #{p18}
where ParaName= #{c} and CalRegNM= #{c0}

mapper.delete:
use forcastflood;
delete  from PARA_FORECAST
where ParaName=#{c} and CalRegNM=#{c0} and ParamId like 'SLHRES%'
